  --background: 0 0% 100%;
  --foreground: 213 37% 11%;
  --border: 210 20% 90%;
  --card: 210 20% 98%;
  --card-foreground: 213 37% 11%;
  --card-border: 210 15% 92%;
  --sidebar: 213 60% 10%;
  --sidebar-foreground: 0 0% 98%;
  --sidebar-border: 213 50% 15%;
  --sidebar-primary: 188 100% 42%;
  --sidebar-primary-foreground: 0 0% 100%;
  --sidebar-accent: 213 50% 15%;
  --sidebar-accent-foreground: 0 0% 98%;
  --sidebar-ring: 188 100% 42%;
  --popover: 0 0% 100%;
  --popover-foreground: 213 37% 11%;
  --popover-border: 210 15% 90%;
  --primary: 188 100% 42%;
  --primary-foreground: 0 0% 100%;
  --secondary: 210 15% 92%;
  --secondary-foreground: 213 37% 11%;
  --muted: 210 15% 95%;
  --muted-foreground: 213 20% 45%;
  --accent: 45 93% 61%;
  --accent-foreground: 213 37% 11%;
  --destructive: 0 84% 48%;
  --destructive-foreground: 0 0% 100%;
  --input: 210 20% 80%;
  --ring: 188 100% 42%;
  --chart-1: 188 100% 42%;
  --chart-2: 45 93% 61%;
  --chart-3: 213 60% 20%;
  --chart-4: 160 70% 38%;
  --chart-5: 280 65% 45%;
  --font-sans: 'Inter', sans-serif;
  --font-heading: 'Poppins', sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: 'JetBrains Mono', monospace;
  --radius: .5rem;
  --shadow-2xs: 0px 1px 2px 0px rgba(10, 26, 47, 0.04);
  --shadow-xs: 0px 1px 3px 0px rgba(10, 26, 47, 0.06);
  --shadow-sm: 0px 2px 4px 0px rgba(10, 26, 47, 0.08);
  --shadow: 0px 4px 8px -2px rgba(10, 26, 47, 0.1);
  --shadow-md: 0px 6px 12px -4px rgba(10, 26, 47, 0.12);
  --shadow-lg: 0px 12px 24px -6px rgba(10, 26, 47, 0.15);
  --shadow-xl: 0px 20px 40px -8px rgba(10, 26, 47, 0.18);
