}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }

  h1, h2, h3, h4, h5, h6 {
    font-family: var(--font-heading);
  }
}

@layer components {
  .text-3d {
    text-shadow: 
      0 1px 0 rgba(10, 26, 47, 0.1),
      0 2px 0 rgba(10, 26, 47, 0.08),
      0 3px 0 rgba(10, 26, 47, 0.06),
      0 4px 0 rgba(10, 26, 47, 0.04),
      0 5px 10px rgba(10, 26, 47, 0.15);
  }

  .text-3d-gold {
    text-shadow: 
      0 1px 0 rgba(245, 197, 66, 0.3),
      0 2px 0 rgba(245, 197, 66, 0.25),
      0 3px 0 rgba(245, 197, 66, 0.2),
      0 4px 0 rgba(245, 197, 66, 0.15),
      0 5px 15px rgba(245, 197, 66, 0.3);
  }

  .text-gradient-gold {
    background: linear-gradient(135deg, #F5C542 0%, #FFE082 50%, #F5C542 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }

  .text-gradient-cyan {
    background: linear-gradient(135deg, #00B8D4 0%, #4DD0E1 50%, #00B8D4 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }

  .gradient-overlay {
    background: linear-gradient(
      180deg,
      rgba(10, 26, 47, 0.9) 0%,
      rgba(10, 26, 47, 0.7) 40%,
      rgba(10, 26, 47, 0.4) 70%,
      transparent 100%
    );
  }

  .gradient-overlay-bottom {
    background: linear-gradient(
      0deg,
      rgba(10, 26, 47, 0.95) 0%,
      rgba(10, 26, 47, 0.6) 40%,
      transparent 100%
    );
  }

  .glass-card {
    background: rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(12px);
    border: 1px solid rgba(255, 255, 255, 0.15);
  }

  .dark .glass-card {
    background: rgba(10, 26, 47, 0.6);
    border: 1px solid rgba(255, 255, 255, 0.08);
  }

  .celebrity-card {
    @apply transition-all duration-300 ease-out;
  }

  .celebrity-card:hover {
    transform: translateY(-8px) scale(1.02);
    box-shadow: 0 20px 40px -12px rgba(0, 184, 212, 0.25);
  }

  .shimmer-bg {
    background: linear-gradient(
      90deg,
      transparent 0%,
      rgba(255, 255, 255, 0.1) 50%,
      transparent 100%
    );
    background-size: 200% 100%;
    animation: shimmer 2s linear infinite;
  }

  .bottom-nav-safe {
    padding-bottom: env(safe-area-inset-bottom, 0px);
  }
}

@layer utilities {
  input[type="search"]::-webkit-search-cancel-button {
    @apply hidden;
  }

  [contenteditable][data-placeholder]:empty::before {
    content: attr(data-placeholder);
    color: hsl(var(--muted-foreground));
    pointer-events: none;
  }

  .no-default-hover-elevate {}
  .no-default-active-elevate {}

  .toggle-elevate::before,
  .toggle-elevate-2::before {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    border-radius: inherit;
    z-index: -1;
  }

  .toggle-elevate.toggle-elevated::before {
    background-color: var(--elevate-2);
  }

  .border.toggle-elevate::before {
    inset: -1px;
  }

  .hover-elevate:not(.no-default-hover-elevate),
  .active-elevate:not(.no-default-active-elevate),
  .hover-elevate-2:not(.no-default-hover-elevate),
  .active-elevate-2:not(.no-default-active-elevate) {
    position: relative;
    z-index: 0;
  }

  .hover-elevate:not(.no-default-hover-elevate)::after,
  .active-elevate:not(.no-default-active-elevate)::after,
  .hover-elevate-2:not(.no-default-hover-elevate)::after,
  .active-elevate-2:not(.no-default-active-elevate)::after {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    border-radius: inherit;
    z-index: 999;
  }

  .hover-elevate:hover:not(.no-default-hover-elevate)::after,
  .active-elevate:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-1);
  }

  .hover-elevate-2:hover:not(.no-default-hover-elevate)::after,
  .active-elevate-2:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-2);
  }

  .border.hover-elevate:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate-2:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate-2:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate:not(.no-hover-interaction-elevate)::after {
    inset: -1px;
  }

  .scrollbar-hide {
    -ms-overflow-style: none;
    scrollbar-width: none;
  }

  .scrollbar-hide::-webkit-scrollbar {
    display: none;
  }
}

client/src/components/stats-section.tsx.content:
import { Users, Star, Globe, Award } from "lucide-react";

const stats = [
  {
    icon: Users,
    value: "500+",
    label: "Celebrity Partners",
    description: "A-list talent across all industries",
  },
  {
    icon: Star,
    value: "10K+",
    label: "Successful Bookings",
    description: "Events and campaigns delivered",
  },
  {
    icon: Globe,
    value: "50+",
    label: "Countries Served",
    description: "Global reach and presence",
  },
  {
    icon: Award,
    value: "98%",
    label: "Client Satisfaction",
    description: "Exceptional service guaranteed",
  },
];

export function StatsSection() {
  return (
    <section className="py-16 md:py-24 bg-skyline-slate dark:bg-muted/30">
      <div className="container mx-auto px-4 md:px-6 lg:px-8">
        <div className="text-center mb-12">
          <h2 className="font-heading font-bold text-3xl md:text-4xl mb-4">
            Trusted by <span className="text-gradient-gold">Industry Leaders</span>
          </h2>
          <p className="text-muted-foreground max-w-2xl mx-auto">
            Our track record speaks for itself. Join thousands of satisfied clients who have elevated their brand with Skyline LTD.
          </p>
        </div>

        <div className="grid grid-cols-2 lg:grid-cols-4 gap-6 md:gap-8">
          {stats.map((stat, index) => (
            <div
              key={stat.label}
              className="text-center p-6 bg-background rounded-xl shadow-sm border animate-fade-in"
              style={{ animationDelay: `${index * 0.1}s` }}
              data-testid={`stat-${stat.label.toLowerCase().replace(/\s+/g, '-')}`}
            >
              <div className="inline-flex items-center justify-center w-12 h-12 rounded-full bg-primary/10 text-primary mb-4">
                <stat.icon className="h-6 w-6" />
              </div>
              <div className="font-heading font-bold text-3xl md:text-4xl text-skyline-navy dark:text-white mb-1">
                {stat.value}
              </div>
              <div className="font-semibold text-foreground mb-1">{stat.label}</div>
              <div className="text-sm text-muted-foreground">{stat.description}</div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}
